Source.BuildSpec:
{
  "version": "0.2",
  "phases": {
    "install": {
      "commands": [
        "npm install -g yarn",
        "yarn global add aws-cdk",
        "yarn global add esbuild"
      ]
    },
    "build": {
      "commands": [
        "yarn",
        "./esbuild.sh",
        "npx cdk synth"
      ]
    }
  },
  "artifacts": {
    "base-directory": "cdk.out",
    "files": "**/*"
  }
}
